#include <iostream>
#include <string>
#include <vector>
using namespace std;

class Solution {
public:
    string shortestPalindrome(string s) {
        string reverse(s.rbegin(), s.rend());
        string mirror = s + "#" + reverse;
        vector<int> prefix(mirror.size(), -1);
        for (int i = 0; i < mirror.size()-1; i++) {
            int j = prefix[i];
            while (j >= 0 && mirror[j+1] != mirror[i+1])
                j = prefix[j];
            prefix[i+1] = j+1;
        }
        int maxPrefix = prefix.back();
        return reverse.substr(0, reverse.size() - maxPrefix - 1) + s;
    }
};

// TLE
class Solution1 {
public:
    string shortestPalindrome(string s) {
        int len = s.size();
        if (len < 2) return s;
        int longest = 0;
        int left, right;
        for (int i = 0; i <= len / 2; ++i) {
            right = left = i;
            while (right < len - 1 && s[right] == s[right+1])
                ++right;
            while (right < len - 1 && left > 0 && s[right+1] == s[left-1]) {
                ++right;
                --left;
            }
            if (left == 0)
                longest = std::max(longest, right + 1);
        }
        string prefix(s.rbegin(), s.rbegin()+len-longest);
        return prefix + s;
    }
};

int main(int argc, const char* args[]) {
    Solution1 s;
    string result = s.shortestPalindrome("");
    cout << result << endl;
}